package com.assignment.operators;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 6;
		x += 6;
		System.out.println(x);
		
		x -= 6;
		System.out.println(x);
		
		x *= 6;
		System.out.println(x);
		
		x /= 6;
		System.out.println(x);
		
		x %= 6;
		System.out.println(x);
	}

}
