package com.example.demo;

public class ExplicitTypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a1 = 10;
		short a2 = (short) a1;
		System.out.println(a2);
		
		double x =  10.0123456789d;  
        float y = (float)x;  
        System.out.println(y);
	}

}
